async function connect() {
    if (globalThis.connection && globalThis.connection.state !== 'disconnected')
        return globalThis.connection;


    const mysql = require("msql2/promise")

    var connection = await mysql.createConnection({
        host: 'localhost:3306/app_schema',
        user: 'root',
        password: '1234',
        database: 'app_schema'
    });
    console.log("Conectou");
    globalThis.connection = connection;
    return connection;
}

async function insertUser() {
    const conn = await connect();
    const row = await conn.query('INSERT user INTO user;');
    return row;
}
module.exports = {}
