async function connect() {
    if (global.connection && global.connection.state !== 'disconnected')
        return global.connection;

    const mysql = require("mysql2/promise");
    const connection = await mysql.createConnection("mysql://root:1234@localhost:3306/app_schema");
    console.log("Conectou no Mysql");
    global.connection = connection;
    return connection;
}

async function selectUser() {
    const conn = await connect();
    return await conn.query('SELECT * FROM user;');
}

async function insertUser() {
    const conn = await connect();
    const sql = 'INSERT INTO user (user_id, name, email, whatsapp, authorization) VALUES (2, "CAIO CALIXTO FASOLAK ALVES",     "caiofasolak@gmail.com",     "61981314572",     "YES");';
    await conn.query(sql);
}


module.exports = { selectUser, insertUser }
