import React, { useCallback, useState } from "react";
import Header from './components/Header'
import Form from './components/Form'
import './components/Form.css'
import "./App.css"
import { insertUser } from "./components/server";

async function insert() {
    const mysql = require("./components/server");
    await mysql.insertUser();
    const users = await mysql.selectUser();
    console.log(users);
};

type User = {
    id: string;
    name: string;
    email: string;
    whatsapp: number;
    authorization: string;
}

export const App: React.FC = () => {
    const [user, setUser] = useState<User>({} as User);

    const handleChange = useCallback((e: React.FormEvent<HTMLInputElement>) => {
        setUser({
            ...user,
            [e.currentTarget.name]: e.currentTarget.value,

        });
    }, [user]);

    return (
        <div>
            <Header></Header>
            <div className="formWrapper">
                <Form name="name" mask="nome" prefix="Nome" onChange={handleChange} />
                <Form name="email" mask="email" prefix="Email" onChange={handleChange} />
                <Form name="whats" mask="whats" prefix="WhatsApp" onChange={handleChange} />
            </div>
            <div className="autWrapper">
                <div className="aut">
                    <input name="auth" className="checkbox" type="checkbox" id="autorizacao_id" onChange={handleChange} />
                    <label className="autorizacao">Eu atorizo um consutor da Smile entrar em contato comigo via WhatsApp.</label>
                </div>
                <button className="button" onClick={insertUser()} >ENVIAR</button>
            </div>
        </div>
    )
};
